例子/文件上传.html.body:
文件上传
package main

import (
    "fmt"
    "io/ioutil"
    "net/http"
    "os"
    "time"

    "github.com/gocolly/colly"
)

/// 生成要提交的表单数据
func generateFormData() map[string][]byte {
    f, _ := os.Open("gocolly.jpg")
    defer f.Close()

    imgData, _ := ioutil.ReadAll(f)

    return map[string][]byte{
        "firstname": []byte("one"),
        "lastname":  []byte("two"),
        "email":     []byte("onetwo@example.com"),
        "file":      imgData,
    }
}
// 启动一个本地的服务端请求
func setupServer() {
    var handler http.HandlerFunc = func(w http.ResponseWriter, r *http.Request) {
        fmt.Println("received request")
        err := r.ParseMultipartForm(10000000)
        if err != nil {
            fmt.Println("server: Error")
            w.WriteHeader(500)
            w.Write([]byte("Internal Server Error"))
            return
        }
        w.WriteHeader(200)
        fmt.Println("server: OK")
        w.Write([]byte("Success"))
    }
    // 监听在8080端口
    go http.ListenAndServe(":8080", handler)
}

func main() {
    // Start a single route http server to post an image to.
    // 启动一个上传图片接口的服务
    setupServer()

    c := colly.NewCollector(colly.AllowURLRevisit(), colly.MaxDepth(5))

    // 返回html之后执行
    c.OnHTML("html", func(e *colly.HTMLElement) {
        fmt.Println(e.Text)
        time.Sleep(1 * time.Second)
        e.Request.PostMultipart("http://localhost:8080/", generateFormData())
    })

    // 请求之前执行
    c.OnRequest(func(r *colly.Request) {
        fmt.Println("Posting gocolly.jpg to", r.URL.String())
    })

    // 开始爬虫
    c.PostMultipart("http://localhost:8080/", generateFormData())
    c.Wait()
}

例子/并发.html.body:
并发
package main

import (
    "fmt"

    "github.com/gocolly/colly"
)

func main() {
    // 初始化默认收集器
    c := colly.NewCollector(
        // 最大深度为2，即在所爬取的页面上的所有链接和这些链接页面上的链接会被访问
        colly.MaxDepth(2),
        colly.Async(true),
    )

    // 限制只有两个线程去访问页面
    c.Limit(&colly.LimitRule{DomainGlob: "*", Parallelism: 2})

    // 处理具有href的a标签
    c.OnHTML("a[href]", func(e *colly.HTMLElement) {
        link := e.Attr("href")
        // 打印链接
        fmt.Println(link)
        // 访问新连接
        e.Request.Visit(link)
    })

    // 开始爬虫
    c.Visit("https://en.wikipedia.org/")
    // 等待所有线程都结束
    c.Wait()
}

