例子/爬虫服务.html.body:
爬虫服务
package main

import (
    "encoding/json"
    "log"
    "net/http"

    "github.com/gocolly/colly"
)

type pageInfo struct {
    StatusCode int
    Links      map[string]int
}

func handler(w http.ResponseWriter, r *http.Request) {
    URL := r.URL.Query().Get("url")
    if URL == "" {
        log.Println("missing URL argument")
        return
    }
    log.Println("visiting", URL)

    c := colly.NewCollector()

    p := &pageInfo{Links: make(map[string]int)}

    // count links
    c.OnHTML("a[href]", func(e *colly.HTMLElement) {
        link := e.Request.AbsoluteURL(e.Attr("href"))
        if link != "" {
            p.Links[link]++
        }
    })

    // extract status code
    c.OnResponse(func(r *colly.Response) {
        log.Println("response received", r.StatusCode)
        p.StatusCode = r.StatusCode
    })
    c.OnError(func(r *colly.Response, err error) {
        log.Println("error:", r.StatusCode, err)
        p.StatusCode = r.StatusCode
    })

    c.Visit(URL)

    // dump results
    b, err := json.Marshal(p)
    if err != nil {
        log.Println("failed to serialize response:", err)
        return
    }
    w.Header().Add("Content-Type", "application/json")
    w.Write(b)
}
// 暴露一个接口，提供一个链接，然后由colly去请求，最后把响应码和链接页面上面的所有链接及其对应的个数返回
func main() {
    // example usage: curl -s 'http://127.0.0.1:7171/?url=http://go-colly.org/'
    addr := ":7171"

    http.HandleFunc("/", handler)

    log.Println("listening on", addr)
    log.Fatal(http.ListenAndServe(addr, nil))
}

例子/链接过滤器.html.body:
链接过滤器
package main

import (
    "fmt"
    "regexp"

    "github.com/gocolly/colly"
)

func main() {
    // 初始化默认收集器
    c := colly.NewCollector(
        // 访问以httpbin.org为根节点但是路径是以h或e开头的链接地址
        // Visit only root url and urls which start with "e" or "h" on httpbin.org
        colly.URLFilters(
            regexp.MustCompile("http://httpbin\\.org/(|e.+)$"),
            regexp.MustCompile("http://httpbin\\.org/h.+"),
        ),
    )

    // 访问具有href属性的a标签
    c.OnHTML("a[href]", func(e *colly.HTMLElement) {
        link := e.Attr("href")
        // P打印href属性
        fmt.Printf("Link found: %q -> %s\n", e.Text, link)
        // 访问该页面上面发现的链接
        // 但是只有满足上面正则过滤规则的链接才会被访问
        c.Visit(e.Request.AbsoluteURL(link))
    })

    // 在访问一个链接之前打印提示信息
    c.OnRequest(func(r *colly.Request) {
        fmt.Println("Visiting", r.URL.String())
    })

    // 开始爬取
    c.Visit("http://httpbin.org/")
}
